import React, { useState } from "react";
import { useSearchReposQuery } from "./api";
import { Link } from "react-router-dom";

export const SearchPage: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const { data, isLoading } = useSearchReposQuery(searchQuery);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    setSearchQuery((e.target as any).search.value);
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input type="text" name="search" placeholder="Search GitHub repos" />
        <button type="submit">Search</button>
      </form>
      {isLoading && <p>Loading...</p>}
      {data?.items.map((repo: any) => (
        <div key={repo.id}>
          <h3>
            <Link to={`/repo/${repo.owner.login}/${repo.name}`}>
              {repo.full_name}
            </Link>
          </h3>
        </div>
      ))}
    </div>
  );
};
